import { useEffect } from 'react'
import { Button, Icon, Tabs } from 'antd'
import getConfig from 'next/config'
import { connect } from 'react-redux'
import Router, { withRouter } from 'next/router'

import Repo from '../components/Repo'

const api = require('../lib/api')

const { publicRuntimeConfig } = getConfig()

let cachedUserRepos, cachedUserStarredRepos

function Index ({ userRepos, userStarredRepos, user, router}) {
    console.log(userRepos, userStarredRepos)

    const tabKey = router.query.key || '1' 

    const handleTabChange = (activeKey) => {
        Router.push(`/?key=${activeKey}`)
    }

    useEffect(() => {})

    if (!user || !user.id) {
        return (
            <div className='root'>
                <p>您还没有登录</p>
                <Button type="primary" href={publicRuntimeConfig.OAUTH_URL}>
                    点击登录
                </Button>
                <style jsx>{`
                    .root {
                        height: 400px;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                    }    
                `}</style>
            </div>
        )
    }

    return (
        <div className="root">
            <div className="user-info">
                <img src={user.avatar_url} alt="user avatar" className="avatar" />
                <span className="login">{user.login}</span>
                <span className="name">{user.name}</span>
                <span className="bio">{user.bio}</span>
                <p className="email">
                    <Icon type="mail" style={{ marginRight: 10 }} />
                    <a href={`mailto:${user.email}`}>{user.email}</a>
                </p>
            </div>
            <div className="user-repos">
                <Tabs activeKey={tabKey} onChange={handleTabChange} animated={false}>
                    {/* Tabs标签页，选项卡切换组件   defaultActiveKey默认选中第一项 activeKey当前激活 tab 面板的 key animated是否使用动画切换Tabs  */}
                    <Tabs.TabPane tab="你的仓库" key="1">
                        {userRepos.map(repo => (
                            <Repo key={repo.id} repo={repo} />
                        ))}
                    </Tabs.TabPane>
                    <Tabs.TabPane tab="你关注的仓库" key="2">
                        {userStarredRepos.map(repo => (
                            <Repo key={repo.id} repo={repo} />
                        ))}
                    </Tabs.TabPane>
                </Tabs>
            </div>
            <style jsx>{`
                .root {
                    display: flex;
                    align-items: flex-start;
                    padding: 20px 0;
                }    
                .user-info {
                    width: 200px;
                    margin-right: 40px;
                    flex-shrink: 0;
                    display: flex;
                    flex-direction: column;
                }
                .login {
                    font-weight: 800;
                    font-size: 20px;
                    margin-top: 20px;
                }
                .name {
                    font-size: 16px;
                    color: #777;
                }
                .bio {
                    margin-top: 20px;
                    color: #333;
                }
                .avatar {
                    width: 100%;
                    border-radius: 5px;  
                }
                .user-repos {
                    flex-grow: 1;
                }
            `}</style>
        </div>
    )
}

const isServer = typeof window === 'undefined'
Index.getInitialProps = async ({ ctx, reduxStore }) => {
    // const result = await axios
    //     .get('/github/search/repositories?q=react')
    //     .then(res => console.log(res))

    const user = reduxStore.getState().user
    if (!user || !user.id) {
        return {
            isLogin: false
        }
    }

    if (!isServer) {
        if (cachedUserStarredRepos && cachedUserRepos) {
            return {
                userRepos: cachedUserRepos,
                userStarredRepos: cachedUserStarredRepos,
            }
        }
    }


    // 获取仓库
    const userRepos = await api.request({
        url: '/user/repos'
    }, ctx.req, ctx.res)

    // 获取认证用户Star的仓库
    const userStarredRepos = await api.request({
        url: '/user/starred'
    }, ctx.req, ctx.res)

    // 浏览器端缓存数据（用户访问了该页面再缓存数据）否则，只要登录过该页面，无论哪个用户，得到的数据都是在服务端已经缓存过的数据，数据都一样了
    if (!isServer) {    
        cachedUserRepos = userRepos.data
        cachedUserStarredRepos = userStarredRepos.data
    }

    return {
        isLogin: true,
        userRepos: userRepos.data,
        userStarredRepos: userStarredRepos.data
    }
}

export default withRouter(connect(function mapStateToProps(state) {
    return {
        user: state.user,
    }
})(Index))
